from flask import Flask

app = Flask(__name__)


def make_bold(function):
    def wrapper_function():
        return f"<b>"+function()+"</b>"
    return wrapper_function

def make_empasis(function):
    def wrapper_function():
        return f"<em>"+function()+"</em>"
    return wrapper_function
   
def make_underline(function):
    def wrapper_function():
        return f"<u>"+function()+"</u>"
    return wrapper_function



@app.route('/')
def hello_world():
    text = "<h1 style='text-align:center'>Hello, World!</h1>"\
            "<p>This is paragraph</p>"\
            "<img src='data:image/jpeg;base64,'>"\
            '<iframe src="https://giphy.com/embed/nR4L10XlJcSeQ" width="480" height="412" frameBorder="0" class="giphy-embed" allowFullScreen></iframe><p><a href="https://giphy.com/gifs/no-cat-nR4L10XlJcSeQ">via GIPHY</a></p>'
    return text


@app.route("/bye")
@make_bold
@make_empasis
@make_underline
def say_bye():
    return "Bye"

@app.route("/username/<name>/<int:number>")
def greet(name, number):
    return f"Hello {name}, you are {number} years old!"




if __name__ == "__main__":
    app.run(debug=True)